<script type="text/javascript">
$(function(){
    $('.sub').click(function(){
        var mobileReg = /^[1][3,4,5,7,8][0-9]{9}$/;
        var mobile = $('input[name="mobile"]').val();
        if(!mobileReg.test(mobile)){
            layer.msg('手机号格式不正确');
            return false;
        }
        $.post('/free/handle', {
            'mobile': mobile
        }, function(res){
           if(res.msg){
               layer.msg(res.msg);
           }
        }, 'json').fail(function(){
            layer.msg('网络错误,请稍后再试');
        });
    });
});
</script>